import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';
import api from '../../services/api';
import moment from 'moment';
 
interface ITask{
    id: number;
    nome: string;
    ra: string;
    dataNasc: Date;
    idade: number;
    Matriculado: boolean;
}
 
const Tasks: React.FC = () => {
 
    const [tasks, setTasks] = useState<ITask[]>([])
 
    useEffect(() => {
        loadTasks()
    }, [])
 
    async function loadTasks() {
        const response = await api.get('/tasks')
        console.log(response);
        setTasks(response.data)
    }

    function formatDate(date: Date){
        return moment(date).format('DD/MM/YYYY')
    }

 
    return (
        
        <div className="container">
            <br />
            <h1>Página de Cadastros</h1>
            <br />
            <Table striped bordered hover className="text-center">
                <thead>
                    <tr>
                    <th>ID</th>
                    <th>Nome</th>
                    <th>Ra</th>
                    <th>Data de Nascimento</th>
                    <th>Idade</th>
                    <th>Situação</th>
                    <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        tasks.map(task => (
                            <tr key={task.id}>
                            <td>{task.id}</td>
                            <td>{task.nome}</td>
                            <td>{task.ra}</td>
                            <td>{formatDate (task.dataNasc) }</td>
                            <td>{task.idade}</td>
                            <td>{task.Matriculado ? "Matriculado": "Matriculado"}</td>
                           

                            <td>
                                    <Button size="sm" variant="primary">Editar</Button>{' '}
                                    <Button size="sm" variant="success">Finalizar</Button>{' '}
                                    <Button size="sm" variant="warning">Visualizar</Button>{' '}
                                    <Button size="sm" variant="danger">Remover</Button>{' '}
                                </td>


                            </tr>
                        ))
                    }
                </tbody>
            </Table>
        </div>
    );
}
 
export default Tasks;
